import Card from 'react-bootstrap/Card';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import { Container } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { useState } from 'react';
import { projects } from '../data/projectsData';
import { useNavigate } from 'react-router-dom';

export const Projects2 = () => {

    const navigate = useNavigate();
    const [clicked, setClicked] = useState(Array(6).fill(false)); // Assuming 6 projects

    const handleCardClick = (index, projectLink) => {

        if (projectLink) {
          navigate(projectLink);
      }
    };

    return (
        <section className="project" id="projects">
            <Container>
                <h3>Recent Projects</h3>
                    <Row xs={1} md={2} className="g-4">
                    {projects.slice(-4).map((project, idx) => (
                        <Col key={idx} onClick={() => handleCardClick(idx, project.link)}>

                            <Card>
                                    <Card.Img variant="top" src={project.imgUrl} />
                                    <Card.Body>
                                    <Card.Title>{project.title}</Card.Title>
                                    <Card.Text>
                                        {project.description}
                                    </Card.Text>
                                    </Card.Body>
                                </Card>

                        </Col>
                    ))}
                    </Row>
                    <Link to='./ProjectsAll' className='view-more-link'>
                        View More..
                    </Link>
            </Container>
        </section>
    );
}
