import { useState } from "react";
import { Container, Row, Col } from "react-bootstrap";
import TrackVisibility from 'react-on-screen';

export const Contact = () => {
    const formInitialDetails = {
        firstName: '',
        lastName: '',
        email: '',
        phone: '',
        message: ''
    };

    const [formDetails, setFormDetails] = useState(formInitialDetails);
    const [buttonText, setButtonText] = useState('Submit');
    const [status, setStatus] = useState({});
    const [isSubmitted, setIsSubmitted] = useState(false);

    const onFormUpdate = (e) => {
      const { name, value } = e.target;
      setFormDetails({
          ...formDetails,
          [name]: value
      });
  };

    const isFormValid = () => {
        let isValid = true;
        const errors = {};

        // Email validation
        const emailRegex = /\S+@\S+\.\S+/;
        if (!emailRegex.test(formDetails.email)) {
            isValid = false;
            errors.email = "Invalid email address.";
        }

        // Simple validation for other fields
        if (!formDetails.firstName.trim()) {
            isValid = false;
            errors.firstName = "First name is required.";
        }

        if (!formDetails.lastName.trim()) {
            isValid = false;
            errors.lastName = "Last name is required.";
        }

        if (!formDetails.phone.trim() || formDetails.phone.length < 10) {
            isValid = false;
            errors.phone = "Invalid phone number, must be at least 10 digits.";
        }

        if (!formDetails.message.trim()) {
            isValid = false;
            errors.message = "Please enter your message.";
        }

        setStatus(errors);
        return isValid;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!isFormValid()) {
            setButtonText('Submit');
            return; // Validation failed
        }

        setButtonText('Sending...');
        try {
            let response = await fetch("http://localhost:5252/contact", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(formDetails),
            });

            if (response.ok) {
                setIsSubmitted(true);
                setStatus({ success: true, message: "Message sent successfully!" });
            } else {
                throw new Error('Failed to send message');
            }
        } catch (error) {
            setStatus({ success: false, message: "Failed to send message. Try again later." });
        }
        setButtonText("Submit");
    };

    const handleNewMessage = () => {
        setIsSubmitted(false);
        setFormDetails(formInitialDetails);
        setStatus({});
    };

    if (isSubmitted && status.success) {
        return (
            <Container>
                <Row>
                    <Col>
                        <div className="thank-you-message">
                            <h2>Message Received!</h2>
                            <p>{status.message}</p>
                            <button onClick={handleNewMessage}>Send a New Message</button>
                        </div>
                    </Col>
                </Row>
            </Container>
        );
    }

    return (
        <section className="contact" id="connect">
            <Container>
                <Row className="align-items-center">
                    <Col size={12} md={6}>
                        <TrackVisibility>
                            {({ isVisible }) =>
                                <div className={isVisible ? "animate__animated animate__fadeIn" : ""}>
                                    <h2>Get In Touch</h2>

                                </div>}
                        </TrackVisibility>
                    </Col>
                    <Col md={6}>
                        <div className="personal-info">
                            <h3>Nicholas Buscemi</h3>
                            <p>nick.buscemi13@gmail.com</p>
                            <p>(631) 316-5618</p>
                            <p>253 North Syracuse Avenue, Massapequa , New York 11758</p>
                        </div>
                    </Col>
                </Row>
            </Container>
        </section>
    );
};
