import { Row, Col, Container, Nav, Tab } from "react-bootstrap";
import projImg1 from '../assets/img/project-img1.png';
import projImg2 from '../assets/img/project-img2.png';
import projImg3 from '../assets/img/project-img3.png';
import { ProjectCard } from "./ProjectCard";
import htmlIcon from '../assets/img/techIcons/htmlIcon.svg'
import javaScriptIcon from '../assets/img/techIcons/icons8-javascript.svg'
import cssIcon from '../assets/img/techIcons/icons8-css.svg'
import reactIcon from '../assets/img/techIcons/icons8-react-native.svg'

export const Projects = () => {

    const projects = [
        {
            title: "Project 1",
            description: "Project 1 Despription",
            imgUrl: projImg1,
        },
        {
            title: "Project 2",
            description: "Project 2 Despription",
            imgUrl: projImg2,
        },
        {
            title: "Project 3",
            description: "Project 3 Despription",
            imgUrl: projImg3,
        },
    ];

    return (
        <section className="project" id="projects">
            <Container>
                <Row>
                    <Col>
                        <h2>Projects</h2>
                        <p>
                            Most of my projects aim to use the most modern and cutting edge technologies to enable a seamless user experience
                        </p>
                        <Tab.Container id="projects-tabs" defaultActiveKey="first">
                            <Nav variant="pills" defaultActiveKey="nav-pills mb-5 justify-content-center align items-center" id="pills-tab">
                                <Nav.Item>
                                    <Nav.Link eventKey="first">Project 1</Nav.Link>
                                </Nav.Item>
                                <Nav.Item>
                                    <Nav.Link eventKey="second">Project 2</Nav.Link>
                                </Nav.Item>
                                <Nav.Item>
                                    <Nav.Link eventKey="third">Project 3</Nav.Link>
                                </Nav.Item>
                            </Nav>
                            <Tab.Content>
                                    <Tab.Pane eventKey="first">
                                        <Row>
                                        {
                                            projects.map((project, index) => {
                                                return (
                                                <ProjectCard
                                                    key={index}
                                                    {...project}
                                                    />
                                                )
                                            })
                                        }
                                        </Row>
                                    </Tab.Pane>
                            </Tab.Content>
                        </Tab.Container>
                    </Col>
                </Row>
            </Container>
        </section>
    )
}
