import React, { useState } from 'react';
import { projects } from '../data/projectsData';
import ProjectCard from './ProjectCard'; // Adjust the path as needed
import { Link } from 'react-router-dom';

export const Projects2 = () => {
  const [clicked, setClicked] = useState(Array(6).fill(false)); // Assuming 6 projects

  const handleCardClick = (index) => {
    const newClicked = [...clicked];
    newClicked[index] = !newClicked[index];
    setClicked(newClicked);
  };

  return (
    <section className="py-8" id="projects">
      <div className="container mx-auto">
        <h3 className="text-3xl font-bold text-center mb-8">Recent Projects</h3>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
          {projects.slice(0, 4).map((project, idx) => (
            <div key={idx} onClick={() => handleCardClick(idx)} className="cursor-pointer">
              <Link to={project.link}>
                <ProjectCard
                  imgSrc={project.imgUrl}
                  title={project.title}
                  description={project.description}
                  link={project.link}
                />
              </Link>
            </div>
          ))}
        </div>
        <div className="flex justify-center mt-8">
          <Link to='./projects-all'>
            <button className="bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-700 transition">
              View All...
            </button>
          </Link>
        </div>
      </div>
    </section>
  );
};
