import React, { useState } from 'react';
import { Col, Row, Container } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { projects } from '../data/projectsData';
import ProjectCard from './ProjectCard'; // Adjust the path as needed
import '../App.css';

export const Projects2 = () => {
    const [clicked, setClicked] = useState(Array(6).fill(false)); // Assuming 6 projects

    const handleCardClick = (index) => {
        const newClicked = [...clicked];
        newClicked[index] = !newClicked[index];
        setClicked(newClicked);
    };

    return (
        <section className="custom-projects-section" id="projects">
            <Container>
                <h3 className="custom-heading">Recent Projects</h3>
                <Row xs={1} md={2} className="g-4">
                    {projects.slice(0, 4).map((project, idx) => (
                        <Col key={idx} onClick={() => handleCardClick(idx)}>
                            <Link to={project.link} style={{ textDecoration: 'none' }}>
                                <ProjectCard
                                    imgSrc={project.imgUrl}
                                    title={project.title}
                                    description={project.description}
                                    link={project.link}
                                />
                            </Link>
                        </Col>
                    ))}
                </Row>
                <Link to='./projects-all' className='custom-view-more-link'>
                    <button className="custom-view-more-button"><span>View All...</span></button>
                </Link>
            </Container>
        </section>
    );
};
